        private static Dictionary<string, int> ConsolePids = new Dictionary<string, int>();
        private static bool Init = false;
        public static bool QuickEdit = false;
        private static int GetConsolePid(string Name)
        {
            if (string.IsNullOrEmpty(Name)) return Process.GetCurrentProcess().Id;
            if (ConsolePids.ContainsKey(Name)) return ConsolePids[Name];
            else return StartConsole(Name);
        }

        private static int StartConsole(string Name)
        {
            ProcessStartInfo psi = new ProcessStartInfo(@"timeout.exe", "/T -1 /NOBREAK")
            {
                UseShellExecute = true
            };

            Process p = Process.Start(psi);

            InitConsole(p.Id, Name, ConsolePids.Count);
            ConsolePids.Add(Name, p.Id);
            return p.Id;
        }

        private static void InitConsole(int pId, string Name, int ConsoleIndex = 0)
        {
            //Thread.Sleep(666);
            string CurrName = Path.GetFileNameWithoutExtension(Assembly.GetEntryAssembly().Location);


            FlipConsole(pId);

            System.Console.CancelKeyPress += new ConsoleCancelEventHandler(KillAllConsoles);
            handler = new ConsoleEventDelegate(ConsoleEventCallback);
            SetConsoleCtrlHandler(handler, true);

            NativeMethods.SetConsoleIcon(GetIcon().Handle);
            var hConsole = NativeMethods.GetConsoleWindow();
            NativeMethods.SendMessage(hConsole, 0x0080, 1, GetIcon().Handle);
            NativeMethods.SendMessage(hConsole, 0x0080, 0, GetIcon().Handle);


            if (!QuickEdit)
                DisableQuickEditMode();

            System.Console.Title = $"[{ConsoleIndex + 1}] {CurrName} - {Name} ({pId})";
            System.Console.Clear();
        }

        private static void KillAllConsoles(object sender, object e)
        {
            foreach (var pId in ConsolePids)
            {
                try
                {
                    var p = Process.GetProcessById(pId.Value);
                    FlipConsole(pId.Value);
                    System.Console.Clear();
                    System.Console.WriteLine("I am getting killed. help.");
                    Thread.Sleep(5000);
                    p.Kill();
                }
                catch { }
            }
        }

        private static IntPtr stdHandle;
        private static SafeFileHandle safeFileHandle;
        private static FileStream fileStream;
        private static StreamWriter standardOutput;
        private static void FlipConsole(int pId)
        {
            if (!Init)
            {
                AppDomain.CurrentDomain.ProcessExit += new EventHandler(KillAllConsoles);
                System.Console.CancelKeyPress += new ConsoleCancelEventHandler(KillAllConsoles);

                handler = new ConsoleEventDelegate(ConsoleEventCallback);
                SetConsoleCtrlHandler(handler, true);
                Init = true;
            }

            if (pId == -1) return;


            var Free = false;
            var Attach = false;

            for (int i = 0; i < 10; i++)
            {
                Free = NativeMethods.FreeConsole();
                if (Free)
                    break;
                Thread.Sleep(25);
            }

            for (int i = 0; i < 10; i++)
            {
                Attach = NativeMethods.AttachConsole(pId);
                if (Attach)
                    break;
                Thread.Sleep(25);
            }
            

            stdHandle = NativeMethods.GetStdHandle(NativeMethods.STD_OUTPUT_HANDLE);
            safeFileHandle = new SafeFileHandle(stdHandle, true);


            fileStream = new FileStream(safeFileHandle, FileAccess.Write);
            standardOutput = new StreamWriter(fileStream)
            {
                AutoFlush = true
            };


            GC.SuppressFinalize(stdHandle);
            GC.SuppressFinalize(safeFileHandle);
            GC.SuppressFinalize(fileStream);
            GC.SuppressFinalize(standardOutput);

            System.Console.SetOut(standardOutput);
           
        }

