        public static void WriteLine(String format, Object arg0, Object arg1, Object arg2, Object arg3, string ConsoleName = "Main", __arglist)
        {
            FlipConsole(GetConsolePid(ConsoleName));
            Object[] objArgs;
            int argCount;

            ArgIterator args = new ArgIterator(__arglist);

            argCount = args.GetRemainingCount() + 4;

            objArgs = new Object[argCount];

            objArgs[0] = arg0;
            objArgs[1] = arg1;
            objArgs[2] = arg2;
            objArgs[3] = arg3;

            for (int i = 4; i < argCount; i++)
            {
                objArgs[i] = TypedReference.ToObject(args.GetNextArg());
            }

            System.Console.WriteLine(format, objArgs);
        }

